data.query:
query($query:QueryExhibitorInput!){
  exhibitorQuery{
    queryCustom(query:$query){
        hasNextPage
        pageIndex
        totalCount
        items {
        activities(num:3){
            id
            title
          }
          banner
          boothArea
          boothInfo
          businesses(num:3){
            id
            remark
            isSample
          }
          category
          company{
            id
            logo
            name
            region
            address
          }
          compId
          exhibitionId
          followCount
          id
          isPopularize
          isLiving
          likeCount
          lookCount
          proCount
          productNumber
          activityCount
          liveCount
          continuouNumber
          likes{
            userInfo
          }
          newestEvaluates{
            id
            content
          }
          products(num:10){
            id
            exhibitorId
            banner
            view3DUrl
          }
          shareCount
          talkCount
          thinkCount
          userCount
          viewCount
        }
      }
    }
}
